{{Use dmy dates|date=December 2017}} {{Infobox sportsperson | name = St. Clair Soleyne | birth_name = | image = | caption = | nationality = Antigua and Barbuda | sport = Sprinting | event = 100 metres | club = | collegeteam = | birth_date = {{birth date and age|1967|9|26|df=yes}} | birth_place = | death_date = | death_place = | height = | weight = }}
