{{Infobox national sports federations |assocname= Austrian Sailing Federation |abbrev= ASF |logo= |logosize= |countryflag=Austria |iocnation=Austria (AUT) |url=www.segelverband.at |sport= [[Sailing]] |othersport1= |othersport2= |othersport3= |othersport4= |historytitle=HISTORY |precedingorganisationsname= |yearfounded=1946 |formernames= |yearclosed= |supersededby= |demographicstitle=DEMOGRAPHICS |noofclubs=97 |noofaffclubs= |membershipsize= |participationlevels= |affliationstitle=AFFILIATIONS |ifed=World Sailing |ifabbrev=WS |ifurl= |ifyearjoined= |nationalolympiccom=[[Austrian Olympic Committee]] |continentalassoc= |nocyearjoined= |nationalparalympiccom=[[Austrian Paralympic Committee]] |npcyearjoined= |regionalgames= |otheraff2= |otheraff3= |otheraff4= |otheraff5= |otheraff6= |noofstaff=6 |electedtitle=ELECTED |patron= |president=Herbert Houf |board= |board1= |board2= |board3= |board4= |board5= |board6= |board7= |board8= |sectitle=Head of Administration |address1=Seestrasse 17b |address2=A-71000 |address3=Neusiedl am See |address4= |country=Austria |chiefexec= |secretarygeneral=Stefan Glanz-Michaelis |olympicteammanager=Georg Fundak |keystaff1position= |keystaff1name= |keystaff2position= |keystaff2name= |keystaff3position= |keystaff3name= |keystaff4position= |keystaff4name= |keystaff5position= |keystaff5name= |keystaff6position= |keystaff6name= |noofvol= |financetitle=FINANCE |companystatus=Association |operatingincome= |sponsors=AutoFrey, Marinepool, Schenker, yachtversicherungen.at, SZIGETI, FSE Robline, Pitter Yachtcharter |regionstitle= |region1= |region2= |region3= |region4= |region5= |region6= |region7= |region8= }}
