{{Infobox Stadium  |
  stadium_name  = Krohnsminde kunstgressbane |
  image    = [[Image:Krohnsminde kunstgressbane.jpg|250px|Krohnsminde kunstgressbane]] |
  location = Danmarks plass,<br/>Bergen |
  built      = 1930-1947 |
  opened     = 1947 |
  seating_capacity   = |
  tenants     = |
  owner = [[Bergen|Bergen Municipality]] |
