{{Infobox |name = NYU Tandon Online |bodystyle = |titlestyle = |abovestyle = background:#cfc; |subheaderstyle = |title = <big>NYU Tandon Online</big> |imagestyle = |captionstyle = | image =[[Image:NYU_Tandon_Online.png|200px]] |labelstyle = |datastyle = | label1 = Established | data1 = 2006 | label2 = Type | data2 = [[Private school|Private]] | label3 = Dean | data3 = [[Katepalli R. Sreenivasan]] | label4 = Dept. Head | data4 = [[Nasir Memon]] | label5 = City | data5 = [[Brooklyn]] | label6 = State | data6 = [[New York (state)|New York]] | label8 = Location | data8 = Online | label9 = Affiliations | data9 = [[New York University Tandon School of Engineering|NYU Tandon School of Engineering]]<br/ > [[New York University]] | label10= Website | data10 = {{url|http://online.engineering.nyu.edu}}
