{{EngvarB|date=July 2017}} {{Use dmy dates|date=July 2017}} {{Infobox scientist | name = Lynn Gladden | honorific_suffix = {{post-nominals|country=GBR|CBE|FRS|FRSC|FInstP|FREng|size=100%}} | birth_name = Lynn Faith Gladden | native_name_lang = | image = <!--(as myimage.jpg, no 'File:')--> | image_size = | alt = | caption = | birth_date = {{Birth date and age|df=yes|1961|7|30}}<ref name=whoswho/> | birth_place = | death_date = <!-- {{Death date and age|df=yes|YYYY|MM|DD|YYYY|MM|DD}} (death date then birth date) --> | death_place = | resting_place = | resting_place_coordinates = <!-- {{Coord|LAT|LONG|type:landmark|display=inline,title}} --> | other_names = | residence = United Kingdom | citizenship = British | nationality = | fields = {{Plainlist| * [[Chemical engineering]] * [[Magnetic resonance imaging]]}} | workplaces = {{Plainlist| * [[University of Cambridge]] * [[University of California, Berkeley]] * [[British Land]]}} | patrons = | education = [[Heathfield School, Pinner]] | alma_mater = {{Plainlist| * [[University of Bristol]] (BSc) * [[University of Oxford]] (PGCE) <!--Keble College, Oxford does NOT award degrees--> * [[University of Cambridge]] (PhD)<!--Trinity College, Cambridge does NOT award degrees--> }} | thesis_title = Structural studies of inorganic glasses | thesis_url = http://ethos.bl.uk/OrderDetails.do?uin=uk.bl.ethos.233932 | thesis_year = 1987 | doctoral_advisor = | academic_advisors = | doctoral_students = | notable_students = | known_for = | author_abbrev_bot = | author_abbrev_zoo = | influences = | influenced = | awards = {{Plainlist| * [[Beilby Medal and Prize]] (1995) * [[Tilden Prize]] (2001) * [[Royal Society Bakerian Medal]] (2014) * [[Top 50 Influential Women in Engineering]] (2016)}} | signature = <!--(filename only)--> | signature_alt = | website = {{URL|https://www.ceb.cam.ac.uk/directory/lynn-gladden}} | footnotes = | spouse = | children = }} '''Lynn Faith Gladden''', {{post-nominals|country=GBR|CBE|FRS|FRSC|FInstP|FREng}}<ref name