{{Infobox company | name = Nanotronics Imaging | logo = | logo_size = | logo_alt = | logo_caption = | logo_padding = | image = | image_size = | image_alt = | image_caption = | trading_name = | native_name = | native_name_lang = <!-- Use ISO 639-1 code, e.g. "fr" for French. For multiple names in different languages, use {{lang|[code]|[name]}}. --> | romanized_name = | former_name = | type = [[Privately held company|Private]] | traded_as = | ISIN = | industry = [[Nanotechnology]] | genre = <!-- Only used with media and publishing companies --> | fate = | predecessor = <!-- or: | predecessors = --> | successor = <!-- or: | successors = --> | founded = 2010<ref name="wired" /> | founders = [[Matthew Putman (scientist)|Matthew Putman]], John Putman | defunct = <!-- {{end date|YYYY|MM|DD}} --> | hq_location = [[Cuyahoga Falls, Ohio]]<ref name="akron">{{cite news|last1=Mackinnon|first1=Jim|title=Nanotronics Imaging in Cuyahoga Falls seeks to revolutionize industries by making a big deal out of the smallest things|url=http://www.ohio.com/business/nanotronics-imaging-in-cuyahoga-falls-seeks-to-revolutionize-industries-by-making-a-big-deal-out-of-the-smallest-things-1.563699|accessdate=5 April 2016|publisher=[[Akron Beacon Journal]]|date=February 2, 2015}}</ref> | hq_location_city = | hq_location_country = | coordinates = | num_locations = | num_locations_year = <!-- Year of num_locations data (when known) --> | area_served = <!-- or: | areas_served = --> | key_people = [[Matthew Putman (scientist)|Matthew Putman]], CEO | products = | brands = | production = | production_year = <!-- Year of production data (when known) --> | services = | revenue = | revenue_year = <!-- Year of revenue data (when known) --> | operating_income = | income_year = <!-- Year of operating_income data (when known) --> | net_income = <!-- or: | profit = --> | net_income_year = <!-- or: | profit_year = --><!-- Year of net_income/profit data (when known) --> | aum = <!-- Only for financial-service comp