{{Infobox spaceflight|auto=all | name = CBERS-4 | image = CBERS line draw.jpeg | image_caption = | insignia = | COSPAR_ID = 2014-079A | SATCAT = 40336 | mission_type = Remote sensing | operator = [[China National Space Administration|CNSA]] / [[National Institute for Space Research|INPE]]<ref name="gsp">{{cite web|url=http://space.skyrocket.de/doc_sdat/cbers-3.htm|title=CBERS 3, 4, 4B / ZY 1D, 1E, 1E2|first=Gunter|last=Krebs|work=Gunter's Space Page|accessdate=7 December 2014}}</ref> | website = [http://www.cbers.inpe.br/ingles/satellites/description_cbers3_4.php CBERS-4] | mission_duration = 3&nbsp;years planned<ref name="EOP">{{cite web|url=https://directory.eoportal.org/web/eoportal/satellite-missions/c-missions/cbers-3-4|title=CBERS-3 & 4 (China-Brazil Earth Resources Satellite) - 2nd Generation Satellite Series|publisher=[[European Space Agency]]|work=Earth Observation Portal|accessdate=7 December 2014}}</ref> | spacecraft_type = [[China–Brazil Earth Resources Satellite program|CBERS]] | spacecraft_bus = [[Phoenix-Eye 1]]<ref name="gsp"/> | manufacturer = | dry_mass = | launch_mass = {{convert|1980|kg|lb}}<ref name="EOP"/> | dimensions = | power = 2,300 watts<ref name="EOP"/> | launch_date = {{start-date|7 December 2014, 03:26|timezone=yes}}&nbsp;UTC | launch_rocket = [[Long March 4B|Chang Zheng 4B]] | launch_site = [[Taiyuan Satellite Launch Center|Taiyuan]] [[Taiyuan Launch Complex 9|LC-9]] | launch_contractor = | orbit_epoch = 25 January 2015, 09:18:29&nbsp;UTC<ref name="n2yo">{{cite web|url=http://www.n2yo.com/satellite/?s=40336|title=CBERS-4 Satellite details 2014-079A NORAD 40336|publisher=N2YO|date=25 January 2015|accessdate=25 January 2015}}</ref> | orbit_reference = [[Geocentric orbit|Geocentric]] | orbit_regime = [[Sun-synchronous orbit|Sun-synchronous]] | orbit_periapsis = {{convert|779|km|mi|}}<ref name="n2yo"/> | orbit_apoapsis = {{convert|781|km|mi|}}<ref name="n2yo"/> | orbit_inclination = 98.54&nbsp;degrees<ref name="n2yo"/> | orbit_semimajor = 