{{COI|date=April 2018}} {{notability|Companies|date=April 2018}} {{Infobox dot-com company |name = Flatiron School |logo = FS_wiki.png |logo_size = |foundation = {{start date and age|2012}} |founder = Adam Enbar, Avi Flombaum |location_city = [[New York City|New York]], [[New York (state)|New York]] |location_country = [[United States|U.S.]] |locations = |area_served = Worldwide |industry = [[Internet industry|Internet]] |num_employees = 151-200 |owner = [[WeWork]] |url = {{URL|https://www.flatironschool.com/}} |language = English |registration = |launch_date = |current_status = Active
