{{notability|organization|date=March 2013}} {{Infobox organization | name = Joule Centre | bgcolor = <!-- header background color --> | fgcolor = <!-- header text color --> | image = Joule_Center_logo.jpg | image_border = | size = 100px | alt = <!-- alt text; see [[WP:ALT]] --> | caption = | map = <!-- optional --> | msize = <!-- map size, optional, default 250px --> | malt = <!-- map alt text --> | mcaption = <!-- optional --> | map2 = | abbreviation = | motto = | predecessor = | successor = | formation = October 2005 | extinction = <!-- date of extinction, optional --> | type = Research institute | status = <!-- ad hoc, treaty, foundation, etc --> | purpose = Energy research | headquarters = Pariser Building | location = Manchester, M13 9PL | coords = <!-- Coordinates of location using a coordinates template --> | region_served = UK | membership = | language = <!-- official languages --> | general = <!-- Secretary General --> | leader_title = Director | leader_name = Prof Peter Crossley | leader_title2 = | leader_name2 = | leader_title3 = | leader_name3 = | leader_title4 = | leader_name4 = | key_people = | main_organ = <!-- gral. assembly, board of directors, etc --> | parent_organization = Northwest Regional Development Agency | affiliations = | budget = | num_staff = | num_volunteers = | website = | remarks = | former name = }}
