if (mpan.Length > 12) {
//Read the check digit into an Integer variable. int intCheckDigit = 0; if (int.TryParse(mpan.Substring(mpan.Length - 1), out intCheckDigit)) {
string strTest = mpan.Substring(mpan.Length - 13, 12); int[] intPrimes = {3, 5, 7, 13, 17, 19, 23, 29, 31, 37, 41, 43}; int productTotal = 0; bool blnError = false;
