| width = {{convert|78.6|in|mm|0|abbr=on}}
| height = {{convert|68.9|in|mm|0|abbr=on}}
| designer = Brandon Faurote (1997)<ref name="faurote">{{cite news |title=Who's Where: Chrysler's new Design VPs |publisher=Car Design News |date=25 May 2007 |url=http://www.cardesignnews.com/site/designers/whos_where/display/store4/item77156/ |deadurl=yes |archiveurl=https://web.archive.org/web/20150402181135/http://www.cardesignnews.com/site/designers/whos_where/display/store4/item77156/ |archivedate=April 2, 2015 |df=mdy-all }}</ref>
