{{Infobox company | name = Raum Energy Inc.. | logo = [[File:Raum-energy-logo.PNG|22opx|Raum Energy Inc.]] | type = Privately owned | genre = | foundation = | founder = Jessie, Darryl | location_city = [[Saskatoon, Canada|Saskatoon]] | location_country = [[Canada]] | location = | locations = | area_served = | key_people = | industry = | products = [[Wind Turbines]] | services = | revenue = | operating_income = | net_income = | assets = | equity = | owner = | num_employees = | parent = | divisions = | subsid = | homepage = www.raumenergy.ca | footnotes = | intl =
