{{Infobox diocese | jurisdiction = Diocese | name = Phú Cường | latin = Dioecesis Phucuongensis | local = Giáo phận Phú Cường | image = | image_size = | image_alt = | caption = | coat = <!-- Coat of arms file name (excluding File: prefix) --> | coat_size = | coat_alt = | flag = <!-- Flag file name (excluding File: prefix) --> | flag_size = | flag_alt = <!---- Locations ----> | country = [[Vietnam]] | territory = | province = [[Roman Catholic Archdiocese of Ho Chi Minh city|Ho Chi Minh City]] | metropolitan = [[Roman Catholic Archdiocese of Ho Chi Minh city|Ho Chi Minh City]] | archdeaconries = | deaneries = | subdivisions = | coordinates = <!-- Use {{coord}} --> <!---- Statistics ----> | area_km2 = 10,855 | area_sqmi = <!-- Area in square miles, automatically converted --> | area_footnotes = | population = 2,953,937 | population_as_of = 2013 | catholics = 141,239 | catholics_percent = 4.8 | parishes = 96 | churches = <!-- Number of churches in the diocese --> | congregations = <!-- Number of congregations in the diocese --> | schools = <!-- Number of church supported schools in the diocese --> | members = <!-- Number of members in the diocese --> <!---- Information ----> | denomination = [[Roman Catholic]] | sui_iuris_church = [[Latin Church]] | rite = [[Roman Rite]] | established = 14 October 1965 | cathedral = Cathedral of the Sacred heart in [[Thủ Dầu Một]] | cocathedral = | patron = <!-- Patron saint(s) of the diocese (or archdiocese) --> | patron_title = <!-- Use to override the default label "Patron saint" --> | priests = <!-- Number of priests in the diocese --> <!---- Current leadership ----> | pope = {{Incumbent pope}} <!-- DO NOT CHANGE. This will update the Popes Automatically as they change --> | patriarch = | major_archbishop = | bishop = [[Joseph Nguyễn Tấn Tước]] | bishop_title = <!-- Type of ordinary: i.e. Bishop or Archbishop. Default is Bishop --> | metro_archbishop = [[Sede vacante]] | coadjutor = | suffragans = <!-- List most senior first (usuall