{{About|the comarca in Badajoz, Spain|the comarca in Córdoba, Spain|Campiña Sur (disambiguation){{!}}Campiña Sur}} {{Infobox settlement <!-- See Template:Infobox settlement for additional fields and descriptions --> | name = Campiña Sur | native_name = {{lang|ext|Campiña Sul}} | native_name_lang = es | settlement_type = [[Comarcas of Extremadura|Comarca]] | image_skyline = | image_alt = | image_caption = | image_flag = | flag_alt = | image_shield = | shield_alt = | nickname = | motto = | image_map = Campiña Sur.png | map_alt = | map_caption = | latd = |latm = |lats = |latNS = | longd = |longm = |longs = |longEW = | coordinates = {{coord|38|57|N|5|51|W|type:city|display=inline,title}} | coordinates_footnotes = | subdivision_type = [[List of sovereign states|Country]] | subdivision_name = {{flag|Spain}} | subdivision_type1 = [[Autonomous_communities_of_Spain|Autonomous community]] | subdivision_name1 = [[Extremadura]] | subdivision_type2 = [[Provinces of Spain|Province]] | subdivision_name2 = [[Badajoz (province)|Badajoz]] | seat_type = [[Capital (political)|Capital]] | seat = [[Llerena, Badajoz|Llerena]] | parts_type = Municipalities | parts_style = list | p1 = [[Ahillones]], [[Azuaga, Badajoz|Azuaga]], [[Berlanga, Badajoz|Berlanga]], [[Campillo de Llerena]], [[Casas de Reina]], [[Fuente del Arco]], [[Granja de Torrehermosa]], [[Higuera de Llerena]], [[Llera, Badajoz|Llera]], [[Llerena, Badajoz|Llerena]], [[Maguilla]], [[Malcocinado]], [[Peraleda del Zaucejo]], [[Puebla del Maestre]], [[Reina, Badajoz|Reina]], [[Retamal de Llerena]], [[Trasierra]], [[Usagre]], [[Valencia de las Torres]], [[Valverde de Llerena]], [[Villagarcía de la Torre]] | leader_party = | leader_title = | leader_name = | area_footnotes = | area_total_km2 = 2698 | elevation_footnotes = | elevation_m = | population_footnotes = <ref name=":0"/> | population_total = 31,583 | population_as_of = | population_density_km2 = auto | population_demonym = | population_note = | blank_name_sec1 = [[List of muni