{{Infobox school
| name = École Belge de Bujumbura
| native_name = 
| address     = Boulevard de Yaranda, Bujumbura, Burundi
| coordinates = {{coord|-3.3938487|29.36024109999994}}
| pushpin_map = Burundi
| website = {{URL|ecolebelge.edu.bi}}
