{{Infobox school |name = Jaunogre secondary school |native_name = Jaunogres vidusskola |latin_name = |logo = |seal_image = |image = |image_size = |alt = |caption = |motto = |motto_translation = |location = |streetaddress = Malkalnes 43 |region = Ogres novads |city = Ogre |state = |province = |county = |postcode = |postalcode = LV-5003 |zipcode = |country = Latvia |country1 = |coordinates = |schooltype = |fundingtype = |type = |religious_affiliation = |religion = |denomination = |patron = |established = |founded = 1972 |opened = |founder = |status = |closed = |locale = |schoolboard = |district = |LEA = |authority = |category = |category_label = |oversight = |oversight_label = |authorizer = |superintendent = |trustee = |specialist = |session = |schoolnumber = |school code = |MOE = |ofsted = |ceeb = |president = |chair = |chairman = |chairperson = |dean = |administrator = |rector = |director = |principal = |principal_label = |principal1 = |principal2 = |principal3 = |principal4 = |viceprincipal = |viceprincipal_label = |viceprincipal1 = |viceprincipal2 = |viceprincipal3 = |viceprincipal4 = |asst principal = |assistant_principals = |campus director = |headmistress = |headmaster = |head of school = |head_teacher = |executive_headteacher = |acting_headteacher = |head = |head_label = |chaplain = |custodian = |staff = |faculty = |teaching_staff = |employees = |key_people = |grades = |years = |gender = |lower_age = |upper_age = |age range = |enrolment = |enrollment = |enrollment_as_of = |students = |sixth_form_students = |pupils = |gradeK = |grade1 = |grade2 = |grade3 = |grade4 = |grade5 = |grade6 = |grade7 = |grade8 = |grade9 = |grade10 = |grade11 = |grade12 = |grade13 = |other = |other_grade_enrollment = |other_grade_label = |international_students = |classes = |avg_class_size = |ratio = |system = |classes offered = |medium = |language = Russian |schedtyp = |schedule = |hours_in_day = |classrooms = |campuses = |campus = |campus size = |area = |campus type = |campus_Bound =