{{Infobox Political party |country = Djibouti | name = Union for National Salvation |native_name = Union pour le Salut National |logo = |president = Ahmed Youssof Houmed |founder = |foundation = 16 January 2013 |dissolution = |headquarters = [[Djibouti City]], [[Djibouti]] |newspaper = |youth_wing = |membership_year = |membership = |ideology = |national = |international = None |colors = |colorcode = #EE5C29 |website = [https://www.facebook.com/USNDJIBOUTI Facebook page] |footnotes =
